app.get("/", (req: express.Request, res: express.Response) => {
  res.send("Hello, world!");
});

app.get("/wakuwaku", (req: express.Request, res: express.Response) => {
  res.send("");
});

app.listen(port, () => {
  console.log(`port ${port} でサーバー起動中`);
});